mapper.select:
select	*
from 	board
where 	contents like #{search}
		and disable='FALSE'
order 	by number desc
limit	#{cri.pageStart},#{cri.perPageNum}

mapper.update:
	update 
		board 
	set 
		disable = #{board.disable}
 	where
 		number = #{board.number}

mapper.select:
select *
	from board
	where author = #{author}
	order by number desc
	limit #{cri.pageStart}, #{cri.perPageNum}

select count(number)
	from board
	where author = #{author}
